function toDataUrl(key) {
	return `data:image/.jpg#;base64,${this.java.base64Encode(key)},{'type':'jpg'}`
}
var Magua = new JavaImporter();
Magua.importPackage(
	Packages.java.io,
	Packages.android.graphics
);
with(Magua) {
	function decodeImg(picBytes, restoreOrder) {
		var img = BitmapFactory.decodeByteArray(picBytes, 0, picBytes.length);
		var imageHeight = img.getHeight();
		var imageWidth = img.getWidth();
		var newImg = Bitmap.createBitmap(imageWidth, imageHeight, Bitmap.Config.ARGB_8888);
		let canvas = new Canvas(newImg);
		const keys = Object.keys(restoreOrder);
		const num = keys.length;
		let blockWidth, blockHeight, extraPixels;
		if (num % 2 === 0) {
			blockWidth = Math.floor(imageWidth / num);
			extraPixels = imageWidth % num;
		} else {
			blockHeight = Math.floor(imageHeight / num);
			extraPixels = imageHeight % num;
		}
		const blocks = [];
		let startX = 0;
		let startY = 0;

		keys.forEach((key, index) => {
			const originalPosition = restoreOrder[key];
			if (originalPosition >= 0 && originalPosition < num) {
				if (num % 2 === 0) {
					const currentBlockWidth = blockWidth + (originalPosition < extraPixels ? 1 : 0);
					blocks[originalPosition] = Bitmap.createBitmap(img, startX, 0, currentBlockWidth, imageHeight);
					startX += currentBlockWidth;
				} else {
					const currentBlockHeight = blockHeight + (originalPosition < extraPixels ? 1 : 0);
					blocks[originalPosition] = Bitmap.createBitmap(img, 0, startY, imageWidth, currentBlockHeight);;
					startY += currentBlockHeight;
				}
			} else {
				this.java.log(`Invalid originalPosition: ${originalPosition}`);
			}
		});
		startX = 0;
		startY = 0;
		Object.keys(blocks)
			.sort((a, b) => a - b)
			.forEach(key => {
				const block = blocks[key];
				if (num % 2 === 0) {
					const currentBlockWidth = block.getWidth();
					canvas.drawBitmap(block, startX, 0, null);
					startX += currentBlockWidth;
				} else {
					const currentBlockHeight = block.getHeight();
					canvas.drawBitmap(block, 0, startY, null);
					startY += currentBlockHeight;
				}
			});
		var newbit = new ByteArrayOutputStream();
		newImg.compress(Bitmap.CompressFormat.PNG, 100, newbit);
		return newbit.toByteArray()
	}
}

