searchUrl:
@js:
const prefix = key.charAt(0);
const offset = '{\{(page-1) *' + (prefix === '%' ? '10}' : (prefix === '@' ? '10}' : '20}')) + '}';
if(prefix === '@'){
    result = 'https://api.lofter.com/newsearch/blog.json?key=' + encodeURI(key.slice(1)) + '&offset=' + offset;
}else if(prefix === '#' || prefix === '＃'){
    result = 'https://api.lofter.com/newsearch/collection.json?key=' + encodeURI(key.slice(1)) + '&offset=' + offset;
}else if(prefix === '%'){
    result = 'https://api.lofter.com/newsearch/grain.json?key={{key}}&offset=' + offset;
}else{
    const header = {
        "headers": {
            "Content-Type": "application/x-www-form-urlencoded;charset=utf-8",
            "deviceid": java.androidId(),
            "if-modified-since": String(new Date()).replace(/(.*?)\s(.*?)\s(.*?)\s(.*?)GMT.*/,'$1, $3 $2 $4 GMT')
        }
    };
    result = 'https://api.lofter.com/newsearch/post.json?key=' + key + '&sortType=0&offset=' + offset + '&limit=20,' + JSON.stringify(header);
}
