var id = url.match(/\d+/g);
if (id && id.length > 0) {
	   var firstNumber = id[0];
    var a = firstNumber.slice(0, 2);
} else {
    java.toast('未获取到封面链接');
}
var b = "https://fm.dameishuwang.net/";
var c = "s.jpg";
var d = "/";
var coverUrl = b + a + d + id + d + id + c;
coverUrl

