ruleBookInfo.tocUrl:
class.BGsectionThree-content@all
@js:
var html = result;
var doc = org.jsoup.Jsoup.parse(html);
var siteList = doc.select("li.site");
var result = {};
var actionMap = {};
for (var i = 0; i < siteList.size(); i++) {
    var site = siteList.get(i);
    var aTags = site.select("a");
    if (aTags.size() >= 2) {
        var secondHref = aTags.get(1).attr("href");
        var secondText = java.t2s(aTags.get(1).text());
        var key = String.fromCharCode(97 + i); // a/b/c...

        result[key] = secondHref;
        actionMap[key] = secondText;
    }
}
var sourceKey = book.getVariable("custom");
var isVariableSet = (sourceKey !== null && sourceKey !== undefined);
if (!isVariableSet) {
    sourceKey = 'a';
    java.longToast("当前未选择目录地址，请通过修改书籍变量来选择（默认为a）");
} else {
    if (!result.hasOwnProperty(sourceKey)) {
        java.longToast("错误：变量值 '" + sourceKey + "' 无效，已回退到默认值a");
        sourceKey = 'a';
    } else {
        java.longToast("更换成功，当前目录地址为：" + actionMap[sourceKey]);
    }
}
result[sourceKey];

