exploreUrl:
@js: var result = []
var push = (title, url, size) => result.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: size
  }
})
push("全部分类",null,1);
tg = java.ajax(source.key);
tag = org.jsoup.Jsoup.parse(tg).select('a[href^=/list]');
tag.forEach(fl => {
  nam = fl.text()
  ul = String(fl.attr('href')).replace(/\/$/,"-{{page}}/");
push(nam,ul,0.25);
})
JSON.stringify(result);

ruleBookInfo.tocUrl:
@js: var id = baseUrl.match(/book\/(.*?)\.html/)[1];
java.put("id",id);
var pages = java.getStringList("option@value",java.ajax(baseUrl));
java.put("page",pages.length);
result = source.key + "/index.php?action=loadChapterPage" + "," + JSON.stringify({
  "body": `id=${id}&page=1`,
  "method": "POST"
});

ruleToc.nextTocUrl:
@js: result = []
id = java.get("id");
pages = java.get("page");
for (let i = 2 ; i <= pages ; i++) {
option = source.key + "/index.php?action=loadChapterPage" + "," + JSON.stringify({
  "body": `id=${id}&page=${i}`,
  "method": "POST"
});
result.push(option);
}
result;

