let type = String(source.getVariable())?.split(",")?.[0] || 0;
java.toast("type："+type)
let list = [];
const typeSort = [
  "novel",
  "comic",
  "video",
  "audio"
 ]

const push = (title, url, type1, type2) => {
  list.push({
    title: title,
    url: url,
    style: {
      layout_flexGrow: type1,
      layout_flexBasisPercent: type2
    }
  });
};

let { novel, audio, comic, video } = discoveryData;
if (typeSort[type] === "novel") {
  let url = `{{getUrl()}}api/novel/app/novel/search?`;
  push(novel.topTitle, null, 1, 1);
  novel.order.forEach((order, i) => {
    push(order, `${url}page={{page}}&size=20&orderType=${i + 1}`, 1, 0.35);
  });
  push(novel.cateTitle, null, 1, 1);
  novel.cate.forEach(cat => {
    push(cat, `${url}category=${cat}&page={{page}}&size=20`, 1, 0.25);
  });
  push(novel.tagTitle, null, 1, 1);
  novel.tag.forEach(tag => {
    push(tag, `${url}includeTags=${tag}&page={{page}}&size=20`, 1, 0.25);
  });
}
else if (typeSort[type] === "audio") {
  push(audio.topTitle, null, 1, 1);
  push(audio.cateTitle, null, 1, 1);
  audio.cate.forEach(cat => {
    push(cat, `{{getUrl()}}api/audio/app/audio/search?category=${cat}&page={{page}}&size=25`, 1, 0.4);
  });
  push(audio.authorTitle, null, 1, 1);
  audio.authors.forEach(author => {
    push(author, `{{getUrl()}}api/audio/app/audio/search?author=${author}&sort=3&searchType=1&page={{page}}&size=25`, 1, 0.25);
  });
  push(audio.rankTitle, null, 1, 1);
  audio.rank.forEach((rank, i) => {
    push(rank, `{{getUrl()}}api/audio/app/audio/rank?page={{page}}&type=${i + 1}&size=25`, 1, 0.4);
  });
}
else if (typeSort[type] === "comic") {
  let url = `{{getUrl()}}api/comic/app/comic/search?`;
  push(comic.topTitle, null, 1, 1);
  comic.area.forEach((area, arigin) => {
    push(area, `${url}origin=${arigin || ""}&page={{page}}&size=25`, 1, 0.39);
  });
  comic.cate.forEach(cat => {
    push(cat, `${url}category=${cat}&page={{page}}&size=25`, 1, 0.25);
  });
  const finished = ["", 1, 0];
  comic.status.forEach((status, i) => {
    push(status, `${url}finished=${finished[i]}&page={{page}}&size=25`, 1, 0.39);
  });
  for (let [tag, value] of Object.entries(comic.tag)) {
    push(tag, `${url}${value}&page={{page}}&size=25`, 1, 0.25);
  }
  push(comic.rankTitle, null, 1, 1);
  comic.rank.forEach((rank, i) => {
    push(rank, `{{getUrl()}}api/comic/app/comic/rank?page={{page}}&type=${i + 1}&size=25`, 1, 0.25);
  });
}
else if (typeSort[type] === "video") {
  let url = `{{getUrl()}}api/video/app/video/search?`;
  push(video.topTitle, null, 1, 1);
  for (let i = 0; i < video.order.length; i++) {
    let order = video.order[i];
    if (!order) continue;
    push(order, `${url}category=&orderType=${i}&page={{page}}&searchType=1&size=40`, 1, 0.25);
  };
  video.cate.forEach((cat, index) => {
    push(cat, `${url}orderType=1&origin=${index + 1}&page={{page}}&searchType=1&size=40`, 1, 0.25);
  });
  push(video.tagTitle, null, 1, 1);
  video.tag.forEach(tag => {
    push(tag, `${url}tag=${tag}&page={{page}}&searchType=1&size=40`, 1, 0.25);
  });
}
else {
	 java.toast("类型出错，请用登录功能重新选择类型！！！")
}

JSON.stringify(list, null, 2)

