exploreUrl:
@js:
var result = [];
var push = (title, url, size) => result.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: size
  }
});
var tagul = org.jsoup.Jsoup.parse(java.ajax(source.key + "/category/")).select('.module li a');
for (i in tagul) {
    push(tagul[i].select('h4').text(),`/e/extend/more/lsmore.php?page={{page}}&line=10&cl=${Number(i)+1}`,0.45);
}
JSON.stringify(result);

