@js:
Array.prototype.add = function (title, url, flexBasis) {
  return this.push({
    title,
    url,
    style: {
      "layout_flexGrow": 1,
      "layout_flexShrink": 1,
      "layout_alignSelf": "auto",
      "layout_flexBasisPercent": flexBasis,
      "layout_wrapBefore": false
    }
  })
}

function generateCategories (arr, str, url) {
	 return str.split("\n").map(x => {
	 	  let [title, value] = x.split("::")
	 	  url = url.replace(/\$\w+/, value)
	 	  return arr.add(title, url, 0.25)
	 	})
}

