var result = [];
var push = (title, tag, size) => result.push({
    title: title,
    url: "/api/v1/json/cate" + "," + JSON.stringify({
  "body": `{\"page\":{\"page\":{{page}},\"pageSize\":10},\"category\":\"comic\",\"sort\":0,\"comic\":{\"status\":-1,\"day\":0,\"tag\":\"${tag}\"},\"video\":{\"year\":0,\"typeId\":0,\"typeId1\":0,\"area\":\"\",\"lang\":\"\",\"status\":-1,\"day\":0},\"novel\":{\"status\":-1,\"day\":0,\"sortId\":0}}`,
  "method": "POST"
}),
    style: {
        layout_flexGrow: 1,
        layout_flexBasisPercent: size
    }
});
var tags = org.jsoup.Jsoup.parse(java.ajax(source.key + '/cate')).select('.tag-container a');
for (let i in tags) {
	let size = i == 0 ? 1 : 0.25;	push(tags[i].text(),tags[i].attr('data-value'),size);
}
JSON.stringify(result);

