ruleToc.chapterList:
@css:
#volumes > .chapter-bar,
#volumes > .jsChapter
@js:
list = [];
list.push({
    CheaperName: "💮↓ 正文卷 ↓💮",
    isVolume: true
});
$ = result;
for (i in $) {
    if (/chapter-bar/.test(String($[i]))) {
        text = "🏮↓〖" + String($[i].text()) + "〗↓🏮";
        list.push({
            CheaperName: text,
            isVolume: true
        });
    } else {  
        $[i].select("li").forEach(a => {
            text = a.select("a").text(); 
            href = a.select("a").attr("href");
            list.push({   
                CheaperName: text,
                CheaperUrl: href
            });
        });
    }
}
list
