ruleToc.chapterList:
@css:
div.chapterbox > table > tbody > tr,
div.chapterbox > table > tbody > tr > td,
div.chapterbox > div.vol_name
@js:
list = [];
$ = result;
for (i in $) {
    if (/vol_name/.test(String($[i]))) {
        text = "🏮 ↓ 〖" + String($[i].text()) + "〗 ↓ 🏮";
        list.push({
            text: text,
            volume: true
        });
    } else {  
        $[i].select("td").forEach(a => {     
            vip = /a/.test(a.select("i"));
            text = a.select("a").text();
            href = a.select("a").attr("href");
            if (vip) {
                symbol = "💎";
            } else {
                symbol = "💮";
            }
            title = symbol + "〖" + text + "〗" + symbol;
            url = `${href},{"webView":true}`
            list.push({   
                title: title,
                isVip: vip,
                url: url
            });
        });
    }
}
