exploreUrl:
@js:
var result = [];
var push = (title, url, size) => result.push({
    title: title,
    url: url,
    style: {
        layout_flexGrow: 1,
        layout_flexBasisPercent: size
    }
});
org.jsoup.Jsoup.parse(java.ajax(source.key)).select('.menu a').toArray().forEach((e, index) => {
	index == 1 ? push("状态", null, 1) : index == 3 ? push("分类", null, 1) : "";
  let url = String(e.attr('href')).replace(/$/, "<,/{{page}}>");
  let size = index == 0 ? 0 : 0.45;
       push(e.text(), url, size);
});
JSON.stringify(result);
