exploreUrl:
@js:
var result = [];
var push = (title, url, size) => result.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: size
  }
});
var urls = [source.key + "/sort1/",source.key + "/top/weekvisit/"];
java.ajaxAll(urls).map((src,index) =>{
	    push(index == 0 ? "分类" : "排行榜", null, 1);
     let sr = org.jsoup.Jsoup.parse(src.body()).select('#smallnav a').toArray().forEach(t => {
     	push(t.text(), t.attr('href'), 0.25);
     	});
});
JSON.stringify(result);

searchUrl:
@js:
cookie.removeCookie(source.key);
var parameters = Array.from(java.ajax(source.key).matchAll(/var\s*(\w+?)\s*=\s*"(\w+?)"/g), m => `${m[1]}=${m[2]}`).join('&');
//java.log(Cookie);
result = "/api/search" + "," + JSON.stringify({
  "body": `q=${key}&${parameters}`,
  "headers": {
    "X-Requested-With": "XMLHttpRequest",
    "Accept-Language": "zh-CN",
    "Cookie": cookie.getCookie(source.key),
    "Accept": "application/json"
  },
  "method": "POST"
});
