ruleSearch.intro:
&emsp;&emsp;{{$.target.abstract}}
<js>
let type_name = java.getString("$.type_name");
let tishi = "---复制下面文字添加至发现规则--";

if(type_name=="用户" || type_name=="日记"){
	result = result + `\n&lrm;\n${tishi}\n&lrm;\n${java.getString("$.target.name||$.target.owner.name")}的日记::${java.getString("$.target.url||$.target.owner.url")}notes?start={{(page-1)*10}\}&type=note`

	}else if(type_name=="讨论"){
		result = result + `\n&lrm;\n🔰所属小组:${java.getString("$.target.owner.name")}\n${tishi}\n&lrm;\n${java.getString("$.target.owner.name")}::https://www.douban.com/group/${java.getString("$.target.owner.id")}/discussion?start={{(page-1)*25}\}`

		}
</js>

