function login(b) {
    if (b == undefined) return true;
    info = result;
    so = getUrl();
    pwd = info.密码;
    imgurl = so + 'user/user/verify.html?' + Math.random();
        if (b) {
        url = so + 'user/user/login.html,';
        body = `username=${info["用户名/邮箱"]}&password=${pwd}&code=${java.getVerificationCode(imgurl)}`;
    } else {
    	java.startBrowser('{{getUrl()}}'+'user/user/reg.html','注册');
    }
    post = JSON.stringify({
        "body": String(body),
        "method": "POST"
    });
    url += post;
    result = java.ajax(url);
    //java.log(result);
    if(result.match(/验证码错误/)){
    	java.toast('\n❗验证码错误❗')
    	}else if(result.match(/登录成功/)){
    	java.toast(info['用户名/邮箱'] +' 登录成功')
    	java.log(url)
    c = cookie.getCookie(getUrl());
    source.putLoginHeader(c)
    }else{
    	java.toast('\n❗登录失败👻')
    	}
    }

function D(){
java.ajax(getUrl()+'user/user/logout.html');
    //输出日志，备用
    java.log('\n用户名/邮箱：'+result['用户名/邮箱']+'\n密码：'+result['密码']);
    cookie.removeCookie(getUrl());
    source.removeLoginHeader();
    result['用户名/邮箱']=result['密码']=''; source.putLoginInfo(JSON.stringify(result));
    cache.deleteMemory('getUrl()');
    java.toast('登出成功')
	}
