function timemd5(time) {
	const {java, source} = this;
let salt = '28c1fdd170a5204386cb1313c7077b34f83e4aaf4aa829ce78c231e05b0bae2c';
let hash = java.md5Encode(time + salt);
	return hash;
}


function gethd(url='',ck='') {
	const {java, source} = this;
let time = java.timeFormatUTC(Date.now(),"yyyy-MM-dd'T'HH:mm:ss'+00:00'",8*60*60*1000);
let hash = this.timemd5(time);
let atk = source.getLoginHeader()||'';
if(atk) atk = String('Bearer '+atk);
let headers = {
    'x-client-time': time,
    	'User-Agent': ua,
    	'accept-language': a_l,
    	'authorization': atk,
    	'app-os': 'Android',
    	'app-os-version': 'Android 14',
    	'app-version': appv,
    'x-client-hash': hash,
    'cookie': ''
    	 }
ck!='' && (headers['cookie']=ck);
url=url.replace("app-api.pixiv.net","210.140.139.155");
url && (headers['host']='app-api.pixiv.net');
if (url=='') return headers;
else if (url.startsWith('data')) return url;
else {
	delete headers['x-client-time'];
	delete headers['x-client-hash'];
	return url + ',' + JSON.stringify({'headers':headers});}
};


function leng(str) {
	let length = 0;
	const fuhao = ',.?!:;()[]"';
	const fuhao2 = '📘';
	for (let char of str) {
		if (fuhao.includes(char) ) length += 0.5;
		else if (fuhao2.includes(char) ) length += 2;
		else length += 1;
		}
		return length;
}

