bookSourceComment:
// byï¼šlandseer & å°å“– & æ¢“æ¾„qwq & Thomaså–²
// å‹¿åˆ æ”¹ä»¥ä¸‹ä»£ç 
var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.javax.crypto.spec,
    Packages.javax.crypto,
    Packages.java.lang,
    Packages.java.util
);
with(javaImport) {
    function bytesToHex(bArr) {
        return bArr.reduce((acc, curr) => acc + ('0' + (curr & 255).toString(16)).slice(-2), '');
    };
    function RandomBytes(str) {
        let bArr = String(str).getBytes();
        Packages.java.security
            .SecureRandom().nextBytes(bArr);
        return bArr;
    };
    function thirdtoken() {
        var bytes = String(JSON.stringify({
            time: new Date().getTime()
        })).getBytes();
        var px2b = String("F1d36851BC0e5943042b261dFcFEd0e5");
        var key = SecretKeySpec(String("5fFf6D94079904826ab080B8179E9376").getBytes(), "AES");
        var rArr = RandomBytes("0123456789ab");
        var iv = GCMParameterSpec(128, rArr);
        var cipher = Cipher.getInstance("AES/GCM/NoPadding");
        cipher.init(1, key, iv);
        cipher.updateAAD(px2b.getBytes());
        var doFinal = cipher.doFinal(bytes);
        if (doFinal == null) {
            return null;
        }
        return px2b + ':' + bytesToHex(rArr) + bytesToHex(doFinal);
    };
    function sub(t, s, e) {
        return Arrays.copyOfRange(t, s, e)
    };
    function decode(str, Key) {
        var str = android.util.Base64.decode(str, 0);
        // è§£å¯†_1
        try { // è‡ªå¸¦æ¥å£
            var key = android.util.Base64.decode(Key, 0);
            var iv = sub(str, 0, 16);
            var bytes = sub(str, 16, str.length);
            var cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
            cipher.init(2, SecretKeySpec(key, "AES"), IvParameterSpec(iv));
            return String(cipher.doFinal(bytes));
        }
        // è§£å¯†_2
        catch (Error) { // é˜…æ–‡æ¥å£
            let utf8Array = Array.from(str).map((b, i) => {
                let c = (((b & 240) >> 4) | ((b & 15) << 4));
                return "Q9*11q^REaDer%Bs1&#@[".charCodeAt(i % 21) ^ c;
            });
            return utf8ArrayToString(utf8Array);
        }
    }
}
// å®ç°utf8Arrayè½¬String
function utf8ArrayToString(utf8Array) {
    let result = '';
    for (let i = 0; i < utf8Array.length; i++) {
        let byte = utf8Array[i];
        if (byte < 0x80) {
            // å•å­—èŠ‚å­—ç¬¦
            result += String.fromCharCode(byte);
        } else if ((byte & 0xE0) === 0xC0) {
            // ä¸¤ä¸ªå­—èŠ‚å­—ç¬¦
            let charCode = (byte & 0x1F) << 6 | (utf8Array[i + 1] & 0x3F);
            result += String.fromCharCode(charCode);
            i++; // è·³è¿‡ä¸‹ä¸€ä¸ªå­—èŠ‚
        } else if ((byte & 0xF0) === 0xE0) {
            // ä¸‰ä¸ªå­—èŠ‚å­—ç¬¦
            let charCode = (byte & 0x0F) << 12 | (utf8Array[i + 1] & 0x3F) << 6 | (utf8Array[i + 2] & 0x3F);
            result += String.fromCharCode(charCode);
            i += 2; // è·³è¿‡ä¸‹ä¸¤ä¸ªå­—èŠ‚
        } else if ((byte & 0xF8) === 0xF0) {
            // å››ä¸ªå­—èŠ‚å­—ç¬¦
            // è¿™é‡Œä¸å¤„ç†å››ä¸ªå­—èŠ‚ä»¥ä¸Šçš„å­—ç¬¦ï¼Œå› ä¸ºå®ƒä»¬ä¸åœ¨UTF-8èŒƒå›´å†…
            throw new Error('Invalid UTF-8 byte sequence');
        }
    }
    return result;
}

